import axios from 'axios';
import { React, useState, useEffect } from 'react';
import { Link } from 'react-router-dom';


const PostDetail = (props) => {
    console.log(props)
    const postID = props.match.params.id;
    const [post, setPost] = useState({});   

    useEffect(() => {
        axios.get(`http://127.0.0.1:8000/posts/${postID}`)
        .then(res => {            
            setPost(res.data);     
        })
        
    }, [postID])

    return (
        <div className='con'>
            <h4>Post Details</h4>
            <div class="row">
                <div class="col-6" style={{fontSize: '18px', fontWeight: 'bold'}}>{post.title}</div>    
                <div class="col-4" style={{fontSize: '18px', fontWeight: 'bold'}}><Link to={`/posts/${post.id}/delete`}>Delete</Link></div>                             
            </div>             
            <p>{post.content}</p>  
        </div>
    )
}



export default PostDetail
