const initialState = {
  course: []
}

const postReducer = (state = initialState, action) => {
  switch (action.type) {
      case 'CREATE_POST':
        {              
          fetch(`http://127.0.0.1:8000/posts/${action.post.username}/newPost`, {
              method: 'POST',
              headers: {
                  'Accept': 'application/json, text/plain, */*',
                  'Content-Type': 'application/json'
              },
              body: JSON.stringify(action.post)
              })                
          return {}
        }  

        case 'UPDATE_COURSE':
        {      
          console.log(JSON.stringify(action.course));
                 
          fetch(`http://127.0.0.1:8000/edu/courses/${action.course.id}/update`, {
              method: 'PUT',
              headers: {
                  'Accept': 'application/json, text/plain, */*',
                  'Content-Type': 'application/json'
              },
              body: JSON.stringify(action.course)
              })
              .then(res => res.json())
              .then(res => console.log(res))
              
          return {}
        }  


      case 'COURSE_LIST': 
        {            
          return {}
        }


      case 'DELETE_COURSE':       
        {
          console.log(action)
          fetch(`http://127.0.0.1:8000/edu/courses/${action.courseID}/delete`, {
              method: 'DELETE',
              headers: {
                  'Accept': 'application/json, text/plain, */*',
                  'Content-Type': 'application/json'
              },
              body: JSON.stringify({
                courseID: action.courseID,
              })
              })
              .then(res => res.json())
              .then(res => console.log(res))

          return {}
        }

        case 'UPDATE_TEACHER_PROFILE':       
        {
          console.log(action.username)
          
          fetch(`http://127.0.0.1:8000/edu/${action.username}/teacherProfile/update`, {
              method: 'PUT',
              body: action.formData
          })
          .then( res => console.log(res))
          .catch(error => console.log(error));
          return {}
        }


      default: return {state}
      };    
}

export default postReducer
