import { useState, useEffect } from "react";
import axios from "axios";
import { Table } from "react-bootstrap";
import { Link } from "react-router-dom";
import authServices from "../store/services/authServices";

const MyPosts = () => {

  const [posts, setPosts] = useState([]);
  const user = authServices().username;
  console.log(user);

  useEffect(() => {
    axios.get("http://127.0.0.1:8000/posts/").then((res) => {
      const myPosts = res.data.filter(
        (post) => post.username.username === user
      );
      console.log(myPosts);
      setPosts(myPosts);
    });
  }, [user]);

  return (
    <div className="mainClass">
      <Table striped bordered hover size="sm">
        <thead>
          <tr>
            <th>CODE</th>
            <th>Course Name</th>
            <th>Teacher</th>
            <th>Hours</th>
            <th>Update</th>
            <th>Delete</th>
            <th>Create Lessons</th>
          </tr>
        </thead>

        <tbody>
          {posts.map((post) => (
            <tr>
              <td>
                <Link to={"/posts/" + post.id}>{post.title}</Link>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
  );
};

export default MyPosts;
