export const createPost = (post) => {
    return (dispatch, getState) => {
        dispatch({            
            type: 'CREATE_POST',
            post: post,        
        })
    }
};

export const updateCourse = (course) => {
    return (dispatch, getState) => {
        dispatch({            
            type: 'UPDATE_COURSE',  
            course: course               
        });
        console.log(course)
    }
};

export const deleteCourse = (courseID) => {
    console.log(courseID);
    return (dispatch, getState) => {
        dispatch({            
            type: 'DELETE_COURSE',    
            courseID: courseID
        })
    }
};


export const updateTeacherProfile = (username, formData) => {
    console.log(username, formData);
    return (dispatch, getState) => {
        dispatch({            
            type: 'UPDATE_TEACHER_PROFILE',    
            username: username,
            formData: formData,
        })
    }
};
